INSERT INTO BY_DOC_RECORD ( ID, BY_DOC_ID, DOC_URL, DOC_NAME, BASE_ID, IS_DELETE, CREATE_TIME, CREATE_USER_ID, COMPANY_TYPE, RECORD_TIME, RECORD_VERSION ) SELECT BY_DOC_RECORD_SEQ.NEXTVAL, T.BY_DOC_ID, T.DOC_URL, T.DOC_NAME, T.BASE_ID, T.IS_DELETE, T.CREATE_TIME, T.CREATE_USER_ID, T.COMPANY_TYPE, T.RECORD_TIME, T.RECORD_VERSION FROM (
SELECT #{item.byDocId, jdbcType =BIGINT} as BY_DOC_ID, #{item.docUrl, jdbcType =VARCHAR} as DOC_URL, #{item.docName, jdbcType =VARCHAR} as DOC_NAME, #{item.baseId, jdbcType =BIGINT} as BASE_ID, #{item.isDelete, jdbcType =INTEGER} as IS_DELETE, #{item.createTime, jdbcType =TIMESTAMP} as CREATE_TIME, #{item.createUserId, jdbcType =BIGINT} as CREATE_USER_ID, #{item.companyType, jdbcType =INTEGER} as COMPANY_TYPE, #{item.recordTime, jdbcType =TIMESTAMP} as RECORD_TIME, #{item.recordVersion, jdbcType =INTEGER} AS RECORD_VERSION from dual
) T
